if typ=="B" then
opt[vaN]=val
elseif typ=="CH" then
opt[vaN]=val
elseif typ=="F" or typ=="I" then
vmi=tonumber(vmi)
if vmi then
val=max(val,vmi)
end
vma=tonumber(vma)
if vma then
val=min(val,vma)
end
opt[vaN]=val
