function Nx.But:Upd()
local typ=self.Typ
if not typ then
return
end
local Ski=Nx.Ski
local f=self.Frm
local tx=f.tex
if self.Sta2 then
local stT=typ[self.Sta2] or typ[1]
local txN=self.Tx or stT.Tx or typ.Tx
if typ.Ski then
txN=Ski:GeT(txN)
else
if txN then
if type(txN)=="string" then
txN=gsub(txN,"%$","Interface\\Icons\\")
else
tx:SetTexture(Nx.U_23(txN))
txN=nil
end
end
end
if txN then
tx:SetTexture(txN)
else
local rgb=stT.RGB
if rgb then
tx:SetTexture(Nx.U_21(rgb))
end
end
if stT.Alp then
tx:SetVertexColor(1,1,1,stT.Alp)
elseif stT.VRGBA then
tx:SetVertexColor(Nx.U_22(stT.VRGBA))
end
local sz=stT.Siz2
if sz then
f:SetWidth(sz)
f:SetHeight(sz)
end
else
if self.Pre then
local txN=self.Tx or typ.Dn
if typ.Ski then
txN=Ski:GeT(txN)
else
if txN then
if type(txN)=="string" then
txN=gsub(txN,"%$","Interface\\Icons\\")
else
tx:SetTexture(Nx.U_23(txN))
txN=nil
end
end
end
if txN then
tx:SetTexture(txN)
else
local rgb=typ.RGBD
if rgb then
tx:SetTexture(Nx.U_21(rgb))
end
end
if typ.AlD then
tx:SetVertexColor(1,1,1,typ.AlD)
elseif typ.VRGBAD then
tx:SetVertexColor(Nx.U_22(typ.VRGBAD))
end
local sz=typ.SiD
if sz then
f:SetWidth(sz)
f:SetHeight(sz)
end
else
local txN=self.Tx or typ.Up
if typ.Ski then
txN=Ski:GeT(txN)
else
if txN then
if type(txN)=="string" then
txN=gsub(txN,"%$","Interface\\Icons\\")
else
tx:SetTexture(Nx.U_23(txN))
txN=nil
end
end
end
if txN then
tx:SetTexture(txN)
if typ.UUV then
local uv=typ.UUV
tx:SetTexCoord(uv[1],uv[2],uv[3],uv[4])
end
else
local rgb=typ.RGBU
if rgb then
tx:SetTexture(Nx.U_21(rgb))
end
end
if typ.AlU then
tx:SetVertexColor(1,1,1,typ.AlU)
elseif typ.VRGBAU then
tx:SetVertexColor(Nx.U_22(typ.VRGBAU))
end
local sz=typ.SiU
if sz then
f:SetWidth(sz)
f:SetHeight(sz)
end
end
end
local of=Nx.But.OvF
if self.Ove then
of:SetPoint("TOPLEFT",f,-1,1)
of:SetWidth(f:GetWidth()+2)
of:SetHeight(f:GetHeight()+2)
