of.tex:SetTexture(Nx.U_22("303080ff"))
else
of.tex:SetTexture(Nx.U_22("101040ff"))
end
of:SetParent(f)
of:Show()
Nx.But.OFO=f
else
if Nx.But.OFO==f then
