function Nx.Map:CFW(frm,bx,by,w,h,dir)
local bw=w
local bh=h
local clW=self.MaW
local clH=self.MaH
local sca=self.ScD
local x=(bx-self.MPXD)*sca+clW/2
local y=(by-self.MPYD)*sca+clH/2
local tX1=0
local tX2=1
local vx0=x-bw*.5
local vx1=vx0
local vx2=vx0+bw
if vx1<0 then
vx1=0
tX1=(vx1-vx0)/bw
end
if vx2>clW then
vx2=clW
tX2=(vx2-vx0)/bw
end
w=vx2-vx1
if w<.3 then
if self.ScF1~=frm then
frm:Hide()
else
frm:SetWidth(.001)
end
return false
end
local tY1=0
local tY2=1
local vy0=y-bh*.5
local vy1=vy0
local vy2=vy0+bh
if vy1<0 then
vy1=0
tY1=(vy1-vy0)/bh
end
if vy2>clH then
vy2=clH
tY2=(vy2-vy0)/bh
end
h=vy2-vy1
if h<.3 then
if self.ScF1~=frm then
frm:Hide()
else
frm:SetWidth(.001)
end
return false
end
frm:SetPoint("TOPLEFT",vx1,-vy1-self.TiH)
frm:SetWidth(w)
frm:SetHeight(h)
if dir==0 then
frm.tex:SetTexCoord(tX1,tX2,tY1,tY2)
else
local t1x,t1y,t2x,t2y,t3x,t3y,t4x,t4y
tX1=tX1-.5
tX2=tX2-.5
tY1=tY1-.5
tY2=tY2-.5
local co=cos(dir)
local si=sin(dir)
t1x=tX1*co+tY1*si+.5
t1y=tX1*-si+tY1*co+.5
t2x=tX1*co+tY2*si+.5
t2y=tX1*-si+tY2*co+.5
t3x=tX2*co+tY1*si+.5
t3y=tX2*-si+tY1*co+.5
t4x=tX2*co+tY2*si+.5
t4y=tX2*-si+tY2*co+.5
frm.tex:SetTexCoord(t1x,t1y,t2x,t2y,t3x,t3y,t4x,t4y)
end
frm:Show()
return true
end
